import React, { useEffect, useState } from 'react'
import Cards from './Cards';
import States from './States';

function Covid() {
    const [ data, setData ] = useState([]);
    const [ state, setStates ] = useState();
    const getCovidData = async () => {
        try {
            const res = await fetch("https://api.covid19india.org/data.json");
            const acutalData = await res.json();
            
            setData(acutalData);
            const states = acutalData.statewise;

            const allStates = states.map((e) => e.state);
            setStates(allStates)
            console.log(allStates);

        } catch (error) {
            console.log(error);
        }
    }




    useEffect(() => { getCovidData() }, [])

    return (
        <div className="container">
            {/* <States stateData={state} /> */}
            <div className="d-flex justify-content-center mb-5">
                <select class="form-control w-50" id="exampleFormControlSelect1">
                    {
                        state.map((e) => {
                            return <option>e</option>;                            
                        })
                    }
                    <option>-- Select State--</option>
                    <option>1</option>
                    <option>2</option>
                    <option>3</option>
                    <option>4</option>
                    <option>5</option>
                </select>
            </div>
            <div className="row gy-4">
                <Cards data= {data} />
            </div>
        </div>
    )
}

export default Covid;
