import axios from 'axios';
import React, { useEffect, useState } from 'react'
import DateTime from '../date-time/DateTime';

function InterNationalCovidUpdate() {
    const headingText = "Welcome to Int. Covid-19 Live Updates";

    const [internationalData, setInternationalData ] = useState([]);
    const getUdpatedData = async () => {
        try {
            const res = await axios("https://api.covid19api.com/summary");
            setInternationalData(res.data.Countries);
            console.log(res.data.Countries[0]);
        } catch (error) {
            console.log(error);
        }
    }

    useEffect(() => { getUdpatedData(); }, []);

    return (
        <div>
            {/* {Header} */}
            <DateTime headingTextData={headingText} />
            <br />
            <div className="container">
                <table className="table table-bordered table-hover table-striped mb-5">
                    <thead className="thead-dark">
                        <tr>
                            <th>#</th>
                            <th>Country</th>
                            <th>Country Code</th>
                            <th>Updated Date</th>
                            <th>New Confirmed</th>
                            <th>New Recovered</th>
                            <th>Total Confirmed</th>
                            <th className="bg-danger">Total Deaths</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            internationalData.map((e, i) => {
                                return <tr key={i}>
                                    <td>{i}</td>
                                    <th>{e.Country}</th>
                                    <td>{e.CountryCode}</td>
                                    <td>{e.Date}</td>
                                    <td>{e.NewConfirmed}</td>
                                    <td>{e.NewRecovered}</td>
                                    <td>{e.TotalConfirmed}</td>
                                    <td className="bg-danger">{e.TotalDeaths}</td>
                                </tr>                                
                            })
                        }
                    </tbody>
                </table>
                <br />
                <br />
            </div>
                        
        </div>

    )
}

export default InterNationalCovidUpdate;
