                <div className="card p-4">
                    <p>Heading</p>
                    <h3>Updates</h3>
                </div>
            </div>
            <div className="col-4">
                <div className="card p-4">
                    <p>Heading</p>
                    <h3>Updates</h3>
                </div>
            </div>
        </div>
        </div>
    )
}

export default Covid;
