import React, { useEffect, useState } from 'react'

function Covid() {
    let indiaData = "";
    const [ data, setData ] = useState([]);
    const getCovidData = async () => {
        try {
            const res = await fetch("https://api.covid19india.org/data.json");
            const acutalData = await res.json();
            setData(acutalData.statewise[0]);
        } catch (error) {
            console.log(error);
        }
    }
    const [updatedData, updatedTime ] = data.lastupdatedtime.split(" ");
    useEffect(() => {
        getCovidData();
    }, [])
    return (
        <div className="container">
            <div className="row gy-4">
                <div className="col-4">
                    <div className="card p-4">
                        <p>Total Active Cases</p>
                        <h3>{ data.active }</h3>
                    </div>
                </div>
                <div className="col-4">
                    <div className="card p-4">
                        <p>Total Confirmed</p>
                        <h3>{ data.confirmed }</h3>
                    </div>
                </div>
                <div className="col-4">
                    <div className="card p-4">
                        <p>Totola Deaths</p>
                        <h3>{ data.deaths }
                        </h3>
                    </div>
                </div>
                <div className="col-4">
                    <div className="card p-4">
                        <p>Total Recovered</p>
                        <h3>{ data.recovered }</h3>
                    </div>
                </div>
                <div className="col-4">
                    <div className="card p-4">
                        <p>Last Updated Time</p>
                        <h3>{ data.lastupdatedtime }</h3>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Covid;
