import moment from 'moment';
import React, { useState } from 'react';

function DigitalClock() {
    // let time = new Date().toLocaleTimeString();
    let time = moment().format("YY MM DD HH:MM:SS");
    console.log(time);
    // const [onlyTime, type ] = time;
    
    // let m = moment().format("YYYY MM DD HH:MM:SS");
    // console.log(m);
    const [cTime, setCTime] = useState(time);
    const updateTime = () => {
        time = new Date().toLocaleTimeString();
        setCTime(time);
    }
    setInterval(updateTime, 1000);

    return (
        <div className="clockTimerContainer text-center py-5">
            <h3 className="text-uppercase mb-5">Digital Clock</h3>
            <h1 className="timeText">{ time }</h1>
        </div>
    )
}

export default DigitalClock;
