import React, { useEffect, useState } from 'react'
import Cards from './Cards';
// import States from './States';
import DateTime from './../date-time/DateTime';
import axios from 'axios';

function Covid() {
    const [ data, setData ] = useState([]);
    const [statesData, setStatesData] = useState([]);

    const [internationalData, setInternationalData ] = useState([]);
    const getCovidData = async () => {
        try {
            const res = await fetch("https://api.covid19india.org/data.json");
            const acutalData = await res.json();            
            setData(acutalData.statewise);
            const states = acutalData.statewise.map(e => e.state);
            setStatesData(states);


            const response = await axios("https://api.covid19api.com/summary");
            const countriesList = response.data.Countries.map(e => e.Country);
            const xyz = response.data.Countries;
            setInternationalData(response.data.Countries);

        } catch (error) {
            console.log(error);
        }
    }

    useEffect(() => { getCovidData() }, [])

    return (
        <>
            <DateTime />
            <div className="container">
                <div className="mb-5">
                    <ul className="list-group">
                        <li className="list-group-item">An item</li>
                        {
                            response.data.Countries.map((e, i) => {
                                return <li className="list-group-item" key={i}>{e}</li>
                            })
                        }
                    </ul>
                </div>

                <div className="d-flex justify-content-center mb-5">
                    <select className="form-control w-50">
                        { 
                            //{/* states.map((e, i) => { */}
                            statesData.map((e, i) =>{
                                return <option key={i}>{e}</option>
                            })
                        }
                    </select>
                </div>
                <div className="row gy-4">
                    <Cards data= {data} />
                </div>
            </div>

        </>
    )
}

export default Covid;
