import React, { useEffect, useState } from 'react'
import Cards from './Cards';
// import States from './States';
import DateTime from './../date-time/DateTime';

function Covid() {
    const dummyStates = [
        "Total", 
        "Andaman and Nicobar Islands", 
        "Andhra Pradesh", 
        "Arunachal Pradesh", 
        "Assam", 
        "Bihar", 
        "Chandigarh", 
        "Chhattisgarh", 
        "Dadra and Nagar Haveli and Daman and Diu", 
        "Delhi", 
        "Goa", 
        "Gujarat", 
        "Haryana", 
        "Himachal Pradesh", 
        "Jammu and Kashmir", 
        "Jharkhand", 
        "Karnataka", 
        "Kerala", 
        "Ladakh", 
        "Lakshadweep", 
        "Madhya Pradesh", 
        "Maharashtra", 
        "Manipur", 
        "Meghalaya", 
        "Mizoram", 
        "Nagaland", 
        "Odisha", 
        "Puducherry", 
        "Punjab", 
        "Rajasthan", 
        "Sikkim", 
        "State Unassigned", 
        "Tamil Nadu", 
        "Telangana", 
        "Tripura", 
        "Uttar Pradesh", 
        "Uttarakhand", 
        "West Bengal"
    ]
    let states;

    const [dummyData, setDummyData]= useState(dummyStates);

    const [ data, setData ] = useState([]);
    const [statesData, setStatesData] = useState([]);
    const getCovidData = async () => {
        try {
            const res = await fetch("https://api.covid19india.org/data.json");
            const acutalData = await res.json();
            
            setData(acutalData.statewise);
            // console.log(acutalData.statewise);
            states = acutalData.statewise.map(e => e.state);
            setStatesData(states);
            // console.log(states);

        } catch (error) {
            console.log(error);
        }
    }

    useEffect(() => { getCovidData() }, [])

    return (
        <>
            <DateTime />
            <div className="container">
                {/* <States stateData={state} /> */}
                <div className="d-flex justify-content-center mb-5">
                    <select className="form-control w-50">
                        { 
                            //{/* states.map((e, i) => { */}
                            statesData.map((e, i) =>{
                                return <option key={i}>{e}</option>
                            })
                        }
                    </select>
                </div>
                <div className="row gy-4">
                    <Cards data= {data} />
                </div>
            </div>

        </>
    )
}

export default Covid;
