    const [ data, setData ] = useState([]);
    const getCovidData = async () => {
        try {
            const res = await fetch("https://api.covid19india.org/data.json");
            const acutalData = await res.json();
            
            setData(acutalData);
            const states = acutalData.statewise;

            const allStates = states.map((e) => e.state);
            setStateData(allStates)
            console.log(allStates);

        } catch (error) {
            console.log(error);
        }
    }




    useEffect(() => { getCovidData() }, [])

    return (
        <>
            <DateTime />
            <div className="container">
                {/* <States stateData={state} /> */}
                <div className="d-flex justify-content-center mb-5">
                    <select className="form-control w-50">
                        {/* { 
                            stateData.map((curList, index) => {
                                return <option key={index}> { curList } </option>;
                            })
                        } */}
                        {/* <option>-- Select State--</option>
                        <option>1</option>
                        <option>2</option>
                        <option>3</option>
                        <option>4</option>
                        <option>5</option> */}
                    </select>
                </div>
                <div className="row gy-4">
                    <Cards data= {data} />
                </div>
            </div>

        </>
    )
}

export default Covid;
